package coreJava;

public class WhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//While Loop
		//1 to 10
		int i=0;
		int j=20;
		
		//Boolean value
		while(i<10) {
			System.out.println(i);
			i++;
		}
		
		
		do
		{
			System.out.println(j);
			j++;
		}while(j<30); //1 loop of execution is guaranteed
		
		
		
	}

}
